return sprintf("Fuel: %3.0f lbs (%d%% tank, %d%% MTOW)",
               arg[0], arg[0]/getprop("/limits/tank")*100,
               getprop("/yasim/gross-weight-lbs")/getprop("/limits/mass-and-balance/maximum-takeoff-mass-lbs")*100);

if ( !getprop("sim/model/ec130/doors/hook/position-norm") ) {
  setprop("/sim/hitches/aerotow/open",1);
  doors.doorsystem.hookexport();
} else {
  doors.doorsystem.hookexport();
  settimer( func {
    setprop("/sim/hitches/aerotow/open",0);
  }, 2);
}
